<?php namespace Functions\Controller; use Zend\Mvc\Controller\AbstractActionController,    Doctrine\ORM\EntityManager,    Zend\Authentication\AuthenticationService;    class FunctionsController extends AbstractActionController{    /**     * @var Doctrine\ORM\EntityManager     */    protected $em;       public function setEntityManager(EntityManager $em)    {        $this->em = $em;    }     public function getEntityManager()    {        if (null === $this->em) {            $this->em = $this->getServiceLocator()->get('Doctrine\ORM\EntityManager');        }        return $this->em;    }     public function indexAction() {        parent::indexAction();    }  }